CREATE OR REPLACE FUNCTION RM558024.FUN_ROW_TO_JSON(
  p_agencia  VARCHAR2,
  p_conta    VARCHAR2,
  p_valor    NUMBER
) RETURN VARCHAR2 IS
BEGIN
  IF p_agencia IS NULL OR p_conta IS NULL OR p_valor IS NULL THEN
    RAISE_APPLICATION_ERROR(-20011,'FUN_ROW_TO_JSON: par√¢metros nulos.');
  END IF;

  RETURN '{'||
         '"agencia":"' || REPLACE(p_agencia,'"','\"') || '",'||
         '"conta":"'   || REPLACE(p_conta  ,'"','\"') || '",'||
         '"valor":'    || TO_CHAR(p_valor,'FM9999990D00') ||
         '}';
END;
