CREATE OR REPLACE PROCEDURE RM558024.PRC_REL_SOMAS IS
BEGIN
  DBMS_OUTPUT.PUT_LINE('AGENCIA | CONTA | VALOR');
  FOR r IN (
    SELECT a.nome_agencia agencia, c.numero_conta conta, f.valor
      FROM FATO_MOVIMENTO f
      JOIN AGENCIA a ON a.id_agencia = f.id_agencia
      JOIN CONTA   c ON c.id_conta   = f.id_conta
     ORDER BY a.nome_agencia, c.numero_conta
  ) LOOP
    DBMS_OUTPUT.PUT_LINE(r.agencia||' | '||r.conta||' | '||TO_CHAR(r.valor,'FM9999990D00'));
  END LOOP;
END;
