TriggerOraclev10g.body:
DECLARE
  v_oper VARCHAR2(10);
BEGIN
  IF INSERTING THEN v_oper := 'INSERT';
  ELSIF UPDATING THEN v_oper := 'UPDATE';
  ELSIF DELETING THEN v_oper := 'DELETE';
  END IF;

  INSERT INTO AUDITORIA_MOV (tabela, operacao, id_registro, data_aud, usuario)
  VALUES ('FATO_MOVIMENTO', v_oper, NVL(:NEW.id_mov,:OLD.id_mov),
          SYSDATE, SYS_CONTEXT('USERENV','SESSION_USER'));
END;
